/**
 * Base64 Plugin for Kitty
 * 
 * Simple plugin for base64 encoding/decoding.
 * Demonstrates working with text transformations.
 */

export const tools = [
  {
    name: 'base64_encode',
    description: 'Encode text to base64 format. Use when users need to encode data for transmission or storage.',
    inputSchema: {
      type: 'object',
      properties: {
        text: {
          type: 'string',
          description: 'Text to encode to base64'
        }
      },
      required: ['text']
    },
    execute: async (params) => {
      try {
        if (!params.text) {
          return 'Error: Text is required';
        }

        const encoded = Buffer.from(params.text, 'utf-8').toString('base64');
        return `Encoded: ${encoded}`;
      } catch (error) {
        return `Error encoding to base64: ${error.message}`;
      }
    }
  },
  {
    name: 'base64_decode',
    description: 'Decode base64 encoded text back to original format. Use when users need to decode base64 data.',
    inputSchema: {
      type: 'object',
      properties: {
        encoded: {
          type: 'string',
          description: 'Base64 encoded string to decode'
        }
      },
      required: ['encoded']
    },
    execute: async (params) => {
      try {
        if (!params.encoded) {
          return 'Error: Encoded text is required';
        }

        const decoded = Buffer.from(params.encoded, 'base64').toString('utf-8');
        return `Decoded: ${decoded}`;
      } catch (error) {
        return `Error decoding from base64: ${error.message}. Make sure the input is valid base64.`;
      }
    }
  }
];
